const mongoose = require("mongoose");
const modelName = "markets";

/**
 * @type {Model.MarketModel}
 */
const Schema = new mongoose.Schema({
  currencies: [],
  marketName: String,
  enabled: { type: Boolean, default: true },
  defaultChipId: String,
  rate: Number,
  isBase: Boolean,
});

/**
 * @type {Model.MarketRepository}
 */
const marketRepository = mongoose.models[modelName] ?? mongoose.model(modelName, Schema);

marketRepository.getAll = () => marketRepository.find().lean().exec();

module.exports = marketRepository;
